/**
 * Custom hooks index
 */

export { default as useReCaptcha } from './useReCaptcha';
export { default as useModalState } from './useModalState';
export { default as useButtonLoading } from './useButtonLoading';
export { default as useTokenizedImage } from './useTokenizedImage';
export { default as useCountdown } from './useCountdown';

import { useState, useEffect, useCallback } from 'react';

const useReCaptcha = (siteKey = '6Lcq_e4qAAAAAEJYKkGw-zQ6CN74yjbiWByLBo6Y') => {
  const [isScriptLoaded, setIsScriptLoaded] = useState(false);
  const [isInitialized, setIsInitialized] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Check if grecaptcha object is available
  useEffect(() => {
    if (!isScriptLoaded) return;

    // Set a timer to check if grecaptcha is fully loaded
    const checkGrecaptchaInterval = setInterval(() => {
      if (window.grecaptcha && window.grecaptcha.enterprise) {
        clearInterval(checkGrecaptchaInterval);
        setIsInitialized(true);
        setIsLoading(false);
      }
    }, 100);

    // Set timeout to avoid infinite checking
    const timeoutId = setTimeout(() => {
      clearInterval(checkGrecaptchaInterval);
      if (!isInitialized) {
        setError('reCAPTCHA initialization failed, please refresh the page');
        setIsLoading(false);
      }
    }, 10000);

    return () => {
      clearInterval(checkGrecaptchaInterval);
      clearTimeout(timeoutId);
    };
  }, [isScriptLoaded, isInitialized]);

  // Load reCAPTCHA script
  useEffect(() => {
    // Check if script is already loaded
    if (document.querySelector('script[src*="recaptcha/enterprise.js"]')) {
      setIsScriptLoaded(true);
      return;
    }

    const script = document.createElement('script');
    script.src = `https://www.google.com/recaptcha/enterprise.js?render=${siteKey}`;
    script.async = true;
    script.id = 'recaptcha-script';

    script.onload = () => {
      setIsScriptLoaded(true);
    };

    script.onerror = (e) => {
      setError('Failed to load reCAPTCHA');
      setIsLoading(false);
    };

    document.body.appendChild(script);

    return () => {
      // Don't remove the script when unmounting, other components may be using it
      // In SPAs, we typically want the reCAPTCHA script to be loaded only once
    };
  }, [siteKey]);

  const getToken = useCallback(async (action) => {
    if (!window.grecaptcha?.enterprise) {
      throw new Error('reCAPTCHA is not ready');
    }

    try {
      const token = await window.grecaptcha.enterprise.execute(
        siteKey,
        { action }
      );
      return token;
    } catch (error) {
      throw error;
    }
  }, [siteKey]);


  const executeReCaptcha = useCallback(async (action) => {
    return getToken(action);
  }, [getToken]);

  return {
    isScriptLoaded,
    isInitialized,
    isLoading,
    error,
    getToken,
    executeReCaptcha
  };
};

export default useReCaptcha;

import { useState, useCallback } from 'react';

/**
 * Hook for managing modal state
 * @param {boolean} initialValue - Initial visibility state of the modal
 * @returns {Array} - [visible, setVisible, show, hide, toggle]
 */
const useModalState = (initialValue = false) => {
  const [visible, setVisible] = useState(initialValue);

  /**
   * Show the modal
   */
  const show = useCallback(() => {
    setVisible(true);
  }, []);

  /**
   * Hide the modal
   */
  const hide = useCallback(() => {
    setVisible(false);
  }, []);

  /**
   * Toggle the modal state
   */
  const toggle = useCallback(() => {
    setVisible(prev => !prev);
  }, []);

  return [visible, setVisible, show, hide, toggle];
};

export default useModalState;

import { useState, useCallback } from 'react';

/**
 * Custom hook for managing button loading state
 * @param {boolean} initialState - Initial loading state
 * @returns {Array} - Loading state and control functions [isLoading, setLoading, withLoading]
 */
const useButtonLoading = (initialState = false) => {
  const [isLoading, setLoading] = useState(initialState);

  /**
   * Higher-order function to handle loading state during async operations
   * @param {Function} asyncFn - Async function to wrap with loading state
   * @returns {Function} - Wrapped function that manages loading state
   */
  const withLoading = useCallback((asyncFn) => async (...args) => {
    if (isLoading) {
      return;
    }

    setLoading(true);

    try {
      const result = await asyncFn(...args);
      return result;
    } catch (error) {
      throw error;
    } finally {
      setLoading(false);
    }
  }, [isLoading]);

  return [isLoading, setLoading, withLoading];
};

export default useButtonLoading;

import { useState, useEffect } from 'react';
import { 
  getToken,
  getFullImageUrl,
  createImageUrlWithToken
} from '../utils/imageUtils';

const useTokenizedImage = (imagePath) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const [tokenUrl, setTokenUrl] = useState('');

  // Generate URL with authentication token
  useEffect(() => {
    if (imagePath) {
      try {
        const url = createImageUrlWithToken(imagePath);
        setTokenUrl(url);
        setError(false);
      } catch (err) {
        setError(true);
      }
    } else {
      setError(true);
    }
    setLoading(false);
  }, [imagePath]);

  // Image handling functions
  const handleLoad = () => {
    setLoading(false);
  };

  const handleError = () => {
    setError(true);
    setLoading(false);
  };

  // Get authentication headers
  const getAuthHeaders = () => {
    const token = getToken();
    if (!token) return {};

    return {
      'Authorization': `Bearer ${token}`,
      'token': token
    };
  };

  return {
    tokenUrl,
    loading,
    error,
    handleLoad,
    handleError,
    getAuthHeaders,
    fullUrl: imagePath ? getFullImageUrl(imagePath) : ''
  };
};

export default useTokenizedImage;

import { useState, useEffect, useCallback } from 'react';

/**
 * Countdown hook
 * @param {number} initialValue - Initial countdown value (seconds)
 * @param {boolean} autoStart - Whether to start countdown automatically
 * @returns {Object} Countdown state and control functions
 */
const useCountdown = (initialValue = 60, autoStart = false) => {
  const [seconds, setSeconds] = useState(autoStart ? initialValue : 0);
  const [isActive, setIsActive] = useState(autoStart);
  const [hasFinished, setHasFinished] = useState(false);

  // Start countdown
  const start = useCallback((value = initialValue) => {
    setSeconds(value);
    setIsActive(true);
    setHasFinished(false);
  }, [initialValue]);

  // Pause countdown
  const pause = useCallback(() => {
    setIsActive(false);
  }, []);

  // Resume countdown
  const resume = useCallback(() => {
    if (seconds > 0) {
      setIsActive(true);
    }
  }, [seconds]);

  // Reset countdown
  const reset = useCallback((value = initialValue) => {
    setSeconds(value);
    setIsActive(false);
    setHasFinished(false);
  }, [initialValue]);

  // Countdown logic
  useEffect(() => {
    let timerId;

    if (isActive && seconds > 0) {
      timerId = setTimeout(() => {
        setSeconds(seconds - 1);
      }, 1000);
    } else if (isActive && seconds === 0) {
      setIsActive(false);
      setHasFinished(true);
    }

    return () => clearTimeout(timerId);
  }, [isActive, seconds]);

  return {
    seconds,
    isActive,
    hasFinished,
    start,
    pause,
    resume,
    reset
  };
};

export default useCountdown;

import { Spin, Image, Avatar } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import { useTokenizedImage } from '../hooks';
import { useState, useEffect, useRef } from 'react';

const ImgWithToken = ({ 
  src, 
  alt = "Image", 
  className = "", 
  style = {}, 
  width, 
  height,
  avatar = false,
  size = 100,
  fallbackIcon = <UserOutlined />,
  fallbackColor = "#1890ff",
  preview = false,
  onClick,
  onLoad,
  onError,
  lazy = true, // 默认启用懒加载
  rootMargin = "100px 0px", // 提前100px开始加载
  threshold = 0.1 // 当图片有10%进入视口时触发加载
}) => {
  const imgRef = useRef(null);
  const [shouldLoad, setShouldLoad] = useState(!lazy); // 如果不使用懒加载，则立即加载

  // 使用自定义 hook 处理图片 URL 和状态
  const {
    tokenUrl,
    loading,
    error,
    handleLoad,
    handleError
  } = useTokenizedImage(src);

  // 初始化Intersection Observer实现懒加载
  useEffect(() => {
    if (!lazy || shouldLoad) return; // 如果已设置加载或不使用懒加载，则跳过

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setShouldLoad(true);
          observer.disconnect();
        }
      },
      {
        rootMargin,
        threshold
      }
    );

    if (imgRef.current) {
      observer.observe(imgRef.current);
    }

    return () => {
      if (imgRef.current) {
        observer.disconnect();
      }
    };
  }, [lazy, shouldLoad, rootMargin, threshold]);

  // 自定义处理函数
  const handleImageLoad = (e) => {
    handleLoad();
    if (onLoad) onLoad(e);
  };

  const handleImageError = (e) => {
    handleError();
    if (onError) onError(e);
  };

  const handleImageClick = (e) => {
    if (onClick) onClick(e);
  };

  // 渲染占位符
  if (lazy && !shouldLoad) {
    const placeholderStyle = {
      width: width || (avatar ? size : '100%'),
      height: height || (avatar ? size : '100px'),
      background: '#f5f5f5',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center'
    };

    return (
      <div ref={imgRef} style={placeholderStyle} className={className}>
        {/* 仅显示占位符，不加载图片 */}
      </div>
    );
  }

  // Avatar模式
  if (avatar) {
    return error ? (
      <Avatar 
        size={size} 
        icon={fallbackIcon} 
        style={{ 
          backgroundColor: fallbackColor,
          ...style
        }}
        className={className}
        onClick={handleImageClick}
      />
    ) : (
      <Avatar 
        size={size}
        src={shouldLoad ? tokenUrl : undefined}
        style={style}
        className={className}
        onLoad={handleImageLoad}
        onError={handleImageError}
        onClick={handleImageClick}
      />
    );
  }

  // 标准图片模式
  return (
    <div 
      ref={imgRef}
      style={{ 
        position: 'relative',
        display: 'inline-block',
        width: width || 'auto',
        height: height || 'auto',
        ...style
      }}
      className={className}
    >
      {loading && (
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          background: '#f5f5f5'
        }}>
          <Spin size="small" />
        </div>
      )}

      {error ? (
        <div 
          style={{
            width: width || '100%',
            height: height || '100px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            background: '#f5f5f5',
            color: '#999',
            fontSize: '12px'
          }}
        >
          图片加载失败
        </div>
      ) : (
        <Image
          src={shouldLoad ? tokenUrl : undefined}
          alt={alt}
          width={width}
          height={height}
          onLoad={handleImageLoad}
          onError={handleImageError}
          onClick={handleImageClick}
          preview={preview}
          style={{ 
            display: loading ? 'none' : 'block',
          }}
        />
      )}
    </div>
  );
};

export default ImgWithToken;
