import { createSlice } from '@reduxjs/toolkit';

// Get storage key with consistent ID from localStorage
const getStorageKey = (key) => {
  // Check if PAGE_INSTANCE_ID exists and is from localStorage (not newly generated)
  // Use simple key names without appending instance ID to ensure finding the same key when refreshing the page
  return key;
};

// Safe method to get values from localStorage
const safeGetItem = (key) => {
  try {
    return localStorage.getItem(getStorageKey(key));
  } catch (error) {
    return null;
  }
};

// Safe method to set values in localStorage
const safeSetItem = (key, value) => {
  try {
    localStorage.setItem(getStorageKey(key), value);
  } catch (error) {
    // Error silently handled
  }
};

// Safe method to remove values from localStorage
const safeRemoveItem = (key) => {
  try {
    localStorage.removeItem(getStorageKey(key));
  } catch (error) {
    // Error silently handled
  }
};

const initialState = {
  token: safeGetItem('token') || null,
  userType: safeGetItem('userType') || null,
  userName: safeGetItem('userName') || 'Admin',
  isAuthenticated: !!safeGetItem('token'),
  loading: false,
  error: null
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    loginStart: (state) => {
      state.loading = true;
      state.error = null;
    },
    loginSuccess: (state, action) => {
      state.token = action.payload.token;
      state.userType = action.payload.userType;
      state.userName = action.payload.userName || 'Admin';
      state.isAuthenticated = true;
      state.loading = false;
      state.error = null;

      // Save to localStorage
      safeSetItem('token', action.payload.token);
      safeSetItem('userType', action.payload.userType);
      safeSetItem('userName', action.payload.userName || 'Admin');
    },
    loginFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
    logout: (state) => {
      state.token = null;
      state.userType = null;
      state.userName = null;
      state.isAuthenticated = false;

      // Clear authentication information from localStorage
      safeRemoveItem('token');
      safeRemoveItem('userType');
      safeRemoveItem('userName');
    }
  }
});

export const { loginStart, loginSuccess, loginFailure, logout } = authSlice.actions;

export default authSlice.reducer;

import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';
import { message } from 'antd';
import { logout } from '../authSlice';

// Get storage key without adding instance ID suffix
const getStorageKey = (key) => {
  // Use simple key names without appending instance ID to ensure token can be found after page refresh
  return key;
};

// Safe method to get values from localStorage
const safeGetItem = (key) => {
  try {
    return localStorage.getItem(getStorageKey(key));
  } catch (error) {
    return null;
  }
};

// Safe method to remove items from localStorage
const safeRemoveItem = (key) => {
  try {
    localStorage.removeItem(getStorageKey(key));
  } catch (error) {
    // Error silently handled
  }
};

// Function to handle token expiration
const handleTokenExpiration = (api) => {
  // Prevent duplicate handling, use a flag to avoid multiple calls
  if (window.__handlingTokenExpiration) {
    return;
  }

  // Set flag to prevent duplicate handling
  window.__handlingTokenExpiration = true;

  // Clear local storage
  safeRemoveItem('token');
  safeRemoveItem('userType');
  safeRemoveItem('userName');

  // Trigger Redux logout action
  api.dispatch(logout());

  // Display message to user
  message.error({
    content: 'Your session has expired. Please login again.',
    duration: 5,
    style: {
      marginTop: '20vh',
      fontSize: '16px',
      fontWeight: 'bold'
    }
  });

  // Redirect to login page
  setTimeout(() => {
    window.location.href = '/login';
    // Reset handling flag
    setTimeout(() => {
      window.__handlingTokenExpiration = false;
    }, 1000);
  }, 2000);
};

// Create a base query
const baseQuery = fetchBaseQuery({
  baseUrl: process.env.NODE_ENV === 'production' 
    ? '/' 
    : 'http://localhost:8080',
  prepareHeaders: (headers, { endpoint }) => {
    // Check if this is an endpoint that doesn't need a token
    const noTokenEndpoints = ['login', 'sendVerificationCode', 'verifyCode', 'resendVerificationCode', 'completeProfile', 'linkGoogleAccount'];
    if (noTokenEndpoints.includes(endpoint)) {
      return headers;
    }

    // Get token from localStorage (using safe method)
    const token = safeGetItem('token');

    if (token) {
      // Only set token header, remove Authorization header
      headers.set('token', token);
    }
    return headers;
  },
});

// Create a wrapped baseQuery to handle token expiration
const baseQueryWithReauth = async (args, api, extraOptions) => {
  const result = await baseQuery(args, api, extraOptions);

  // Check for errors
  if (result.error) {
    // Extract data from error
    const errorData = result.error.data;

    // Check if it's a token expiration error
    if (errorData) {
      const { code, msg, data } = errorData;

      // Extended error code detection, compatible with multiple expiration scenarios
      const isTokenExpired = 
        (code === 3000) || 
        (code === 401) || 
        (msg === 'Not logged in or login expired') ||
        (msg && msg.toLowerCase().includes('token expired')) ||
        (msg && msg.toLowerCase().includes('invalid token')) ||
        (data && typeof data === 'string' && data.includes('authentication is required'));

      if (isTokenExpired) {
        // Call local handling function directly
        handleTokenExpiration(api);
      }
    }
  }

  return result;
};

// Create base API configuration
export const baseApi = createApi({
  reducerPath: 'api',
  baseQuery: baseQueryWithReauth,
  // Common tag types
  tagTypes: ['User', 'Auth', 'Coach', 'MemberSubscriptionRequests', 'MemberUnreadCount'],
  // Endpoints will be defined in specific API slices
  endpoints: () => ({}),
});

import { baseApi } from './baseApi';

// Extend base API with authentication related endpoints
export const authApi = baseApi.injectEndpoints({
  endpoints: (builder) => ({
    // Login
    login: builder.mutation({
      query: (data) => {
        const { headers, ...requestData } = data;

        // Check if this is a Google login request
        if (requestData.googleToken) {
          return {
            url: '/auth/google-login',
            method: 'POST',
            body: { token: requestData.googleToken },
            headers: {
              ...headers,
              'Content-Type': 'application/json'
            }
          };
        }

        return {
          url: '/auth/login',
          method: 'POST',
          body: requestData,
          headers: {
            ...headers,
            'Content-Type': 'application/json' // Ensure content type is set
          }
        };
      },
      // Add response transformation and error handling
      transformResponse: (response, meta, arg) => {
        return response;
      },
      transformErrorResponse: (response, meta, arg) => {
        return response;
      },
      onQueryStarted: async (arg, { dispatch, queryFulfilled }) => {
        try {
          const { data } = await queryFulfilled;
        } catch (error) {
          // Error handling is automatic
        }
      },
      invalidatesTags: ['Auth']
    }),

    // Complete profile
    completeProfile: builder.mutation({
      query: (data) => ({
        url: '/auth/google-login/complete-profile',
        method: 'POST',
        body: data,
        headers: {
          'Content-Type': 'application/json'
        }
      }),
      invalidatesTags: ['Auth', 'User']
    }),

    // Logout
    logout: builder.mutation({
      query: () => ({
        url: '/auth/logout',
        method: 'POST'
      }),
      invalidatesTags: ['Auth', 'User']
    }),

    // Get current user information
    getCurrentUser: builder.query({
      query: () => '/auth/user',
      providesTags: ['Auth']
    }),

    // Register new user
    register: builder.mutation({
      query: (data) => {
        const { headers, ...requestData } = data;
        return {
          url: '/auth/register',
          method: 'POST',
          body: requestData,
          headers: {
            ...headers,
            'Content-Type': 'application/json'
          }
        };
      },
      transformResponse: (response, meta, arg) => {
        return response;
      },
      transformErrorResponse: (response, meta, arg) => {
        return response;
      }
    }),

    // Send verification code
    sendVerificationCode: builder.mutation({
      query: (data) => {
        const { headers, ...requestData } = data;
        return {
          url: '/auth/sendCode',
          method: 'POST',
          body: requestData,
          headers
        };
      }
    }),

    // Verify code
    verifyCode: builder.mutation({
      query: (data) => {
        const { headers, ...requestData } = data;
        return {
          url: '/auth/verifyCode',
          method: 'POST',
          body: requestData,
          headers
        };
      }
    }),

    // Resend verification code
    resendVerificationCode: builder.mutation({
      query: (data) => {
        const { headers, ...requestData } = data;
        return {
          url: '/auth/resendCode',
          method: 'POST',
          body: requestData,
          headers
        };
      },
      // Custom handling logic to prevent duplicate requests
      onQueryStarted: async (arg, { dispatch, queryFulfilled, getCacheEntry }) => {
        try {
          // Duplicate request prevention logic will be automatically handled by RTK Query
          await queryFulfilled;
        } catch (error) {
          // Error handling is automatic
        }
      }
    })
  })
});

// Export auto-generated hooks
export const {
  useLoginMutation,
  useLogoutMutation,
  useGetCurrentUserQuery,
  useRegisterMutation,
  useSendVerificationCodeMutation,
  useVerifyCodeMutation,
  useResendVerificationCodeMutation,
  useCompleteProfileMutation
} = authApi;

/**
 * Custom hooks index
 */

export { default as useReCaptcha } from './useReCaptcha';
export { default as useModalState } from './useModalState';
export { default as useButtonLoading } from './useButtonLoading';
export { default as useTokenizedImage } from './useTokenizedImage';
export { default as useCountdown } from './useCountdown';

import { useState, useCallback } from 'react';

/**
 * Hook for managing modal state
 * @param {boolean} initialValue - Initial visibility state of the modal
 * @returns {Array} - [visible, setVisible, show, hide, toggle]
 */
const useModalState = (initialValue = false) => {
  const [visible, setVisible] = useState(initialValue);

  /**
   * Show the modal
   */
  const show = useCallback(() => {
    setVisible(true);
  }, []);

  /**
   * Hide the modal
   */
  const hide = useCallback(() => {
    setVisible(false);
  }, []);

  /**
   * Toggle the modal state
   */
  const toggle = useCallback(() => {
    setVisible(prev => !prev);
  }, []);

  return [visible, setVisible, show, hide, toggle];
};

export default useModalState;

import { useState, useCallback } from 'react';

/**
 * Custom hook for managing button loading state
 * @param {boolean} initialState - Initial loading state
 * @returns {Array} - Loading state and control functions [isLoading, setLoading, withLoading]
 */
const useButtonLoading = (initialState = false) => {
  const [isLoading, setLoading] = useState(initialState);

  /**
   * Higher-order function to handle loading state during async operations
   * @param {Function} asyncFn - Async function to wrap with loading state
   * @returns {Function} - Wrapped function that manages loading state
   */
  const withLoading = useCallback((asyncFn) => async (...args) => {
    if (isLoading) {
      return;
    }

    setLoading(true);

    try {
      const result = await asyncFn(...args);
      return result;
    } catch (error) {
      throw error;
    } finally {
      setLoading(false);
    }
  }, [isLoading]);

  return [isLoading, setLoading, withLoading];
};

export default useButtonLoading;

// Image utility functions

/**
 * Get full server URL
 * @returns {string} Server base URL
 */
export const getBaseUrl = () => {
  return process.env.NODE_ENV === 'production' 
    ? '/' 
    : 'http://localhost:8080';
};

/**
 * Build complete image URL
 * @param {string} path - Image relative path
 * @returns {string} Complete image URL
 */
export const getFullImageUrl = (path) => {
  if (!path) return '';

  // If already a complete URL, return directly
  if (path.startsWith('http://') || path.startsWith('https://')) {
    return path;
  }

  // Ensure path starts with /
  const normalizedPath = path.startsWith('/') ? path : `/${path}`;
  return `${getBaseUrl()}${normalizedPath}`;
};

/**
 * Get authentication token from sessionStorage
 * @returns {string|null} Authentication token or null
 */
export const getToken = () => {
  // Get storage key with page ID
  const getStorageKey = (key) => {
    return window.PAGE_INSTANCE_ID ? `${key}_${window.PAGE_INSTANCE_ID}` : key;
  };

  return sessionStorage.getItem(getStorageKey('token'));
};

/**
 * Create request headers with authentication information
 * @returns {Object} Request headers object with authentication info
 */
export const getAuthHeaders = () => {
  const token = getToken();

  if (!token) {
    return {};
  }

  return {
    'Authorization': `Bearer ${token}`,
    'token': token
  };
};

/**
 * Create image URL object with source and authentication headers
 * @param {string} path - Image path
 * @returns {Object} Object with src and headers
 */
export const createImageSrcObject = (path) => {
  const url = getFullImageUrl(path);

  return {
    src: url,
    crossOrigin: 'anonymous',
    headers: getAuthHeaders()
  };
};

/**
 * Create image URL with authentication parameters
 * For scenarios where request headers cannot be set, like img tags
 * @param {string} path - Image path
 * @returns {string} Complete URL with authentication parameters
 */
export const createImageUrlWithToken = (path) => {
  const baseUrl = getFullImageUrl(path);
  return baseUrl;
};

/**
 * Create CSS background image style with authentication information
 * @param {string} path - Image path
 * @returns {Object} CSS style object
 */
export const createBackgroundImageStyle = (path) => {
  const imageUrl = createImageUrlWithToken(path);

  return {
    backgroundImage: `url(${imageUrl})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center'
  };
};

import { useState, useEffect } from 'react';
import { 
  getToken,
  getFullImageUrl,
  createImageUrlWithToken
} from '../utils/imageUtils';

const useTokenizedImage = (imagePath) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const [tokenUrl, setTokenUrl] = useState('');

  // Generate URL with authentication token
  useEffect(() => {
    if (imagePath) {
      try {
        const url = createImageUrlWithToken(imagePath);
        setTokenUrl(url);
        setError(false);
      } catch (err) {
        setError(true);
      }
    } else {
      setError(true);
    }
    setLoading(false);
  }, [imagePath]);

  // Image handling functions
  const handleLoad = () => {
    setLoading(false);
  };

  const handleError = () => {
    setError(true);
    setLoading(false);
  };

  // Get authentication headers
  const getAuthHeaders = () => {
    const token = getToken();
    if (!token) return {};

    return {
      'Authorization': `Bearer ${token}`,
      'token': token
    };
  };

  return {
    tokenUrl,
    loading,
    error,
    handleLoad,
    handleError,
    getAuthHeaders,
    fullUrl: imagePath ? getFullImageUrl(imagePath) : ''
  };
};

export default useTokenizedImage;

import { useState, useEffect, useCallback } from 'react';

/**
 * Countdown hook
 * @param {number} initialValue - Initial countdown value (seconds)
 * @param {boolean} autoStart - Whether to start countdown automatically
 * @returns {Object} Countdown state and control functions
 */
const useCountdown = (initialValue = 60, autoStart = false) => {
  const [seconds, setSeconds] = useState(autoStart ? initialValue : 0);
  const [isActive, setIsActive] = useState(autoStart);
  const [hasFinished, setHasFinished] = useState(false);

  // Start countdown
  const start = useCallback((value = initialValue) => {
    setSeconds(value);
    setIsActive(true);
    setHasFinished(false);
  }, [initialValue]);

  // Pause countdown
  const pause = useCallback(() => {
    setIsActive(false);
  }, []);

  // Resume countdown
  const resume = useCallback(() => {
    if (seconds > 0) {
      setIsActive(true);
    }
  }, [seconds]);

  // Reset countdown
  const reset = useCallback((value = initialValue) => {
    setSeconds(value);
    setIsActive(false);
    setHasFinished(false);
  }, [initialValue]);

  // Countdown logic
  useEffect(() => {
    let timerId;

    if (isActive && seconds > 0) {
      timerId = setTimeout(() => {
        setSeconds(seconds - 1);
      }, 1000);
    } else if (isActive && seconds === 0) {
      setIsActive(false);
      setHasFinished(true);
    }

    return () => clearTimeout(timerId);
  }, [isActive, seconds]);

  return {
    seconds,
    isActive,
    hasFinished,
    start,
    pause,
    resume,
    reset
  };
};

export default useCountdown;

import { message } from 'antd';

/**
 * Get redirect path based on user type
 * @param {string} userType - User type parsed from login response message
 * @returns {string} Redirect path
 */
export const getRedirectPath = (userType) => {
  switch (userType) {
    case 'admin':
      return '/admin/dashboard';
    case 'member':
      return '/member/dashboard';
    case 'coach':
      return '/coach/dashboard';
    default:
      return '/';
  }
};

/**
 * Check if user is authenticated based on localStorage token
 * @returns {boolean} User authentication status
 */
export const isAuthenticated = () => {
  const token = localStorage.getItem('token');
  return !!token;
};

// Alias for isAuthenticated to resolve import error in Login.jsx
export const isUserAuthenticated = isAuthenticated;

/**
 * Get user type from localStorage
 * @returns {string|null} User type or null if not found
 */
export const getUserType = () => {
  return localStorage.getItem('userType');
};

/**
 * Check if the current route is accessible for the user's role
 * @param {string} path - Current route path
 * @returns {boolean} Whether the route is accessible
 */
export const isRouteAccessible = (path) => {
  const userType = getUserType();

  // Public routes accessible to anyone
  if (path === '/login' || path === '/register' || path === '/verify-code' || path === '/' || path.startsWith('/reset-password')) {
    return true;
  }

  // Check authentication
  if (!isAuthenticated()) {
    return false;
  }

  // Role-based route check
  if (path.startsWith('/member') && userType === 'member') {
    return true;
  }

  if (path.startsWith('/coach') && userType === 'coach') {
    return true;
  }

  if (path.startsWith('/admin') && userType === 'admin') {
    return true;
  }

  // Access denied for this role
  return false;
};

/**
 * Handle unauthorized access by redirecting and showing a message
 * @param {string} path - Attempted path
 */
export const handleUnauthorizedAccess = (path) => {
  message.error('You do not have permission to access this page');

  // Redirect based on user type or to login if not authenticated
  const userType = getUserType();

  if (!isAuthenticated()) {
    window.location.href = '/login';
    return;
  }

  // Redirect to appropriate dashboard based on role
  if (userType === 'member') {
    window.location.href = '/member/dashboard';
  } else if (userType === 'coach') {
    window.location.href = '/coach/dashboard';
  } else if (userType === 'admin') {
    window.location.href = '/admin/dashboard';
  } else {
    window.location.href = '/login';
  }
};

/**
 * Extract user type from login response data
 * @param {object} data - Login response data
 * @returns {string} User type
 */
export const getUserTypeFromData = (data) => {
  // 适应新的数据结构，从userInfo中获取role
  if (data && data.userInfo && data.userInfo.role) {
    return data.userInfo.role;
  }

  // 兼容旧版数据结构
  return data?.role || 'member';
};

import { motion } from 'framer-motion';

const PageTransition = ({ children, isVisible, direction = 'horizontal', noAnimation = false }) => {
  if (noAnimation) {
    return (
      <div style={{
        width: '100%',
        height: '100%',
      }}>
        {children}
      </div>
    );
  }

  const getInitialX = () => {
    if (direction === 'horizontal') {
      return isVisible ? 100 : -100;
    }
    return 0;
  };

  const getInitialY = () => {
    if (direction === 'vertical') {
      return isVisible ? 50 : -50;
    }
    return 0;
  };

  const getExitX = () => {
    if (direction === 'horizontal') {
      return isVisible ? -100 : 100;
    }
    return 0;
  };

  const getExitY = () => {
    if (direction === 'vertical') {
      return isVisible ? -50 : 50;
    }
    return 0;
  };

  return (
    <motion.div
      initial={{ opacity: 0, x: getInitialX(), y: getInitialY() }}
      animate={{ opacity: 1, x: 0, y: 0 }}
      exit={{ opacity: 0, x: getExitX(), y: getExitY() }}
      transition={{ duration: 0.5, ease: "easeInOut" }}
      style={{
        position: 'absolute',
        width: '100%',
        height: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
      }}
    >
      {children}
    </motion.div>
  );
};

export default PageTransition;

import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useLocation } from 'react-router-dom';
import { useLoginMutation } from '../store/api/authApi';
import { Form, Input, Button, Card, Typography, message, Divider } from 'antd';
import { ExclamationCircleFilled } from '@ant-design/icons';
import { useForm, Controller } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { useReCaptcha, useButtonLoading } from '../hooks';
import { GoogleLogin } from '@react-oauth/google';

import { loginStart, loginSuccess, loginFailure } from '../store/authSlice';
import { getRedirectPath, getUserTypeFromData, isUserAuthenticated } from '../utils/routeUtils';
import PageTransition from '../components/PageTransition';

const { Title } = Typography;

// Form validation schema
const schema = yup.object({
  email: yup.string().email('Please enter a valid email address').required('Email address is required'),
  password: yup.string().required('Password is required'),
}).required();

const Login = () => {
  const location = useLocation();
  const isRegisterPage = location.pathname === '/register';

  // Use custom hooks
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, error } = useSelector((state) => state.auth);

  // State management
  const [expiredError, setExpiredError] = useState(false);

  // Check if there's a profile completion message
  useEffect(() => {
    if (location.state?.message) {
      message.success(location.state.message);
      // Clear message to avoid showing again on refresh
      navigate(location.pathname, { replace: true, state: {} });
    }
  }, [location.state, navigate]);

  // Use RTK Query hook
  const [login, loginResult] = useLoginMutation();

  // Use custom button loading state hook
  const [isLoading, setLoading, withLoading] = useButtonLoading(false);

  // Use custom reCAPTCHA hook
  const { executeReCaptcha, isScriptLoaded, isInitialized, error: recaptchaError } = useReCaptcha('6Lcq_e4qAAAAAEJYKkGw-zQ6CN74yjbiWByLBo6Y');

  // Form control
  const { control, handleSubmit, formState: { errors } } = useForm({
    resolver: yupResolver(schema),
    defaultValues: {
      email: '',
      password: '',
    }
  });

  // Handle form submission
  const onSubmit = async (formData) => {
    if (!isScriptLoaded || !isInitialized) {
      dispatch(loginFailure(recaptchaError || 'reCAPTCHA not ready, please refresh the page and try again'));
      // Show reCAPTCHA error
      message.error(recaptchaError || 'reCAPTCHA not ready, please refresh the page and try again');
      return;
    }

    try {
      // Manually set loading state
      setLoading(true);

      // Get reCAPTCHA token
      const recaptchaToken = await executeReCaptcha('login');

      // Call RTK Query's login mutation
      dispatch(loginStart());

      const data = await login({
        ...formData,
        headers: {
          'X-Recaptcha-Token': recaptchaToken,
          'X-Action': 'login'
        }
      }).unwrap();

      if (data.code === 0) {
        // Get user info, adapting to new data structure
        const userInfo = data.data?.userInfo || {};

        // Get token and role from userInfo
        const token = userInfo.token;
        const userType = userInfo.role;
        const userName = userInfo.userName || 'Admin';

        if (!token) {
          const errorMsg = 'Login successful but no token received, please contact administrator';
          dispatch(loginFailure(errorMsg));
          message.error(errorMsg);
          return;
        }

        // Dispatch login success action
        dispatch(loginSuccess({
          token,
          userType,
          userName
        }));

        // Show login success message
        message.success('Login Successful');

        // Use utility function to get redirect path
        const redirectPath = getRedirectPath(userType);

        // Navigate directly
        navigate(redirectPath, { replace: true });
      } else if (data.code === 3004) {
        message.info('Please complete your profile to continue');
        navigate('/complete-profile', { 
          state: { email: data.data.email },
          replace: true 
        });
        return;
      } else {
        // Ensure we use the error message from backend
        const errorMessage = data.msg || 'Login failed, please try again later';

        dispatch(loginFailure(errorMessage));

        // Show error message
        message.error(errorMessage);
      }
    } catch (error) {
      let errorMessage = 'Login failed';

      // Check different possible error object structures
      if (error.data) {
        errorMessage = error.data.msg || errorMessage;
      } else if (error.response && error.response.data) {
        errorMessage = error.response.data.msg || errorMessage;
      } else if (error.message) {
        errorMessage = error.message;
      }

      // Only update Redux state
      dispatch(loginFailure(errorMessage));

      // Show error message
      message.error(errorMessage);
    } finally {
      // Reset loading state when done
      setLoading(false);
    }
  };

  // Check if redirected to login page due to session expiration
  useEffect(() => {
    // First check if we need to show expiration alert
    const shouldShowExpiredAlert = 
      (location.state && location.state.expired) || 
      sessionStorage.getItem('tokenExpirationHandled') === 'true';

    if (shouldShowExpiredAlert) {
      // Show expiration alert
      setExpiredError(true);

      // Clear state and marker to avoid showing again
      if (location.state && location.state.expired) {
        navigate(location.pathname, { replace: true, state: {} });
      }
      sessionStorage.removeItem('tokenExpirationHandled');

      // Automatically close the alert after 5 seconds
      const timer = setTimeout(() => {
        setExpiredError(false);
      }, 5000);

      return () => clearTimeout(timer);
    }
  }, [location, navigate]);

  // Add Google login handler
  const handleGoogleLoginSuccess = async (credentialResponse) => {
    try {
      // Manually set loading state
      setLoading(true);

      // Call RTK Query's login mutation
      dispatch(loginStart());

      // Send Google login verification info
      const data = await login({
        googleToken: credentialResponse.credential,
        headers: {
          'X-Action': 'google-login'
        }
      }).unwrap();

      if (data.code === 0) {
        // Get user info, adapting to new data structure
        const userInfo = data.data?.userInfo || {};

        // Get token and role from userInfo
        const token = userInfo.token;
        const userType = userInfo.role;
        const userName = userInfo.userName || 'Admin';

        if (!token) {
          const errorMsg = 'Login successful but no token received, please contact administrator';
          dispatch(loginFailure(errorMsg));
          message.error(errorMsg);
          return;
        }

        // Dispatch login success action
        dispatch(loginSuccess({
          token,
          userType,
          userName
        }));

        // Show login success message
        message.success('Google Login Successful');

        // Use utility function to get redirect path
        const redirectPath = getRedirectPath(userType);

        // Navigate directly
        navigate(redirectPath, { replace: true });
      } else if (data.code === 3004) {
        message.info('Please complete your profile to continue');
        navigate('/complete-profile', { 
          state: { email: data.data.email },
          replace: true 
        });
        return;
      } else if (data.code === 3006) {
        // Google email exists but not via Google account login
        message.info('This email is already registered. Please confirm if you want to link your Google account.');
        navigate('/link-google-account', { 
          state: { 
            email: data.data.email,
            googleToken: credentialResponse.credential 
          },
          replace: true 
        });
        return;
      } else {
        // Ensure we use the error message from backend
        const errorMessage = data.msg || 'Google login failed, please try again later';

        dispatch(loginFailure(errorMessage));

        // Show error message
        message.error(errorMessage);
      }
    } catch (error) {
      let errorMessage = 'Google login failed';

      // Check different possible error object structures
      if (error?.data) {
        errorMessage = error.data.msg || errorMessage;
      } else if (error?.response && error.response.data) {
        errorMessage = error.response.data.msg || errorMessage;
      } else if (error?.message) {
        errorMessage = error.message;
      }

      // Only update Redux state
      dispatch(loginFailure(errorMessage));

      // Show error message
      message.error(errorMessage);
    } finally {
      // Reset loading state when done
      setLoading(false);
    }
  };

  const handleGoogleLoginError = () => {
    message.error('Google login failed. Please try again.');
  };

  return (
    <PageTransition isVisible={isRegisterPage}>
      <div className="flex justify-center items-center min-h-screen" style={styles.container}>
        {expiredError && (
          <div style={styles.errorToast}>
            <ExclamationCircleFilled style={{ color: '#ff4d4f', marginRight: '8px' }} />
            Your session has expired. Please login again.
          </div>
        )}

        <Card className="w-full max-w-md shadow-md" style={styles.box}>
          <div className="text-center mb-6">
            <Title level={2} style={styles.title}>Sign In</Title>
          </div>

          <Form layout="vertical" onFinish={handleSubmit(onSubmit)}>
            <Form.Item
              label="Email"
              validateStatus={errors.email ? 'error' : ''}
              help={errors.email?.message}
            >
              <Controller
                name="email"
                control={control}
                render={({ field }) => <Input {...field} placeholder="Enter your email address" />}
              />
            </Form.Item>

            <Form.Item
              label="Password"
              validateStatus={errors.password ? 'error' : ''}
              help={errors.password?.message}
            >
              <Controller
                name="password"
                control={control}
                render={({ field }) => <Input.Password {...field} placeholder="Enter your password" />}
              />
            </Form.Item>

            <Form.Item style={{ textAlign: 'center' }}>
              <Button
                type="primary"
                htmlType="submit"
                style={{
                  ...styles.signInButton,
                  width: '80%',
                  margin: '0 auto',
                }}
                loading={isLoading || loginResult.isLoading}
                disabled={isLoading || loginResult.isLoading || !isScriptLoaded || !isInitialized}
              >
                {!isScriptLoaded || !isInitialized ? 'reCAPTCHA Loading...' : 'Sign In'}
              </Button>
            </Form.Item>
          </Form>

          <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '15px' }}>
            <GoogleLogin
              onSuccess={handleGoogleLoginSuccess}
              onError={handleGoogleLoginError}
              useOneTap={false}
              locale="en"
              theme="outline"
              type="standard"
              size="large"
              text="signin_with"
              shape="rectangular"
            />
          </div>

          <div style={styles.footerText}>
            <span>Don't have an account? </span>
            <Button 
              type="link" 
              onClick={() => navigate('/register')} 
              style={styles.signUpLink}
              disabled={isLoading || loginResult.isLoading}
            >
              Sign Up
            </Button>
          </div>
        </Card>
      </div>
    </PageTransition>
  );
};

